using Katas;
using Katas.Strings;
using NUnit.Framework;

namespace Tests.Katas.Strings
{
    [TestFixture]
    public class CodilityCodingTest
    {
        [Test]
        [TestCase("011100", 7)]
        [TestCase("011111", 9)]
        [TestCase("1101", 6)]
        [TestCase("1", 1)]
        [TestCase("10", 2)]
        [TestCase("", 4081)]
        public void CalculateByParseToBigInt(string input, int expected)
        {
            var result = new ColdilityCoding().ParseToBigInt(input);
            Assert.AreEqual(expected, result);
        }

        [Test]
        [TestCase("011100", 7)]
        [TestCase("011111", 9)]
        [TestCase("1101", 6)]
        [TestCase("1", 1)]
        [TestCase("10", 2)]
        [TestCase("", 4081)]
        public void CalculateByCheckAndChangeChars(string input, int expected)
        {
            var result = new ColdilityCoding().CheckAndChangeChars(input);
            Assert.AreEqual(expected, result);
        }
    }
}
